SELECT * FROM ( SELECT ROWNUM rnum, result.* FROM ( SELECT P.code pc_code, P.brand pc_brand, P.type pc_type, P.price pc_price, P.DELIVERYPRICE pc_deliprice, P.grade pc_grade, C.name cpu_name, C.kind cpu_kind, C.speed cpu_speed, R.speed ram_speed, M.ramlimit mainboard_ramlimit, H.space hdd_space, H.speed hdd_speed, S.space ssd_space, S.speed ssd_speed, G.kind graphic_kind, O.name os_name FROM PC P JOIN CPU C ON P.cpu = C.code JOIN RAM R ON P.ram = R.code JOIN MAINBOARD M ON P.mainboard = M.code LEFT OUTER JOIN HDD H ON P.hdd = H.code LEFT OUTER JOIN SSD S ON P.ssd = S.code JOIN GRAPHIC G ON P.graphic = G.code JOIN OS O ON P.os = O.code
WHERE P.type = #{pcType, javaType=String, jdbcType=VARCHAR}
) result ) WHERE rnum BETWEEN #{start, javaType=int, jdbcType=NUMERIC} AND #{end, javaType=int, jdbcType=NUMERIC}

